import "./App.css"
import Home from "./pages/homepage/Home.jsx";
import Watch from "./pages/watch/watch.jsx";
import Register from "./pages/register/register.jsx";
import Login from "./pages/login/login.jsx";
import Anonymous from "./pages/anonymous/anonymous.jsx"
import Search from "./pages/search/search.jsx"
import Profile from "./pages/profile/profile.jsx";

import {
    BrowserRouter as Router,
    Switch,
    Route,
    Redirect,

  } from "react-router-dom";



import React from 'react'
import { useContext } from "react";
import { AuthContext } from "./authContext/AuthContext";
import Details from "./pages/details/details.jsx";

const App = () => {

    const {user} = useContext(AuthContext);
    console.log(user);
    return (
    //v 5.2.0
        <Router>
            <Switch>

                <Route exact path = "/">


                   {user ? <Home user= {user}/> : <Redirect to="/anonymous"/>} 

                </Route>

                <Route path = "/register">
                   {!user ? <Register/> : <Redirect to="/"/>} 
                </Route>

                <Route path = "/anonymous">
                   <Anonymous/> 
                </Route>



                <Route path = "/login">
                {!user ? <Login/> : <Redirect to="/"/>} 
                </Route>


                {
                    user && (
                        <>
                                <Route path = "/movies">
                                    <Home type = "movie"/>
                                </Route>

                                <Route path = "/series">
                                    <Home type = "Series"/>
                                </Route>

                                <Route path = "/watch">
                                    <Watch/>
                                </Route>


                                <Route path = {["/search/:searchID","/search"]}>
                                    <Search/>
                                    </Route>

                                <Route path = "/details/:imdbID">
                                    <Details/>
                                </Route>

                                <Route path = "/otherusers/:currentUserID">
                                    <Otherusers/>
                                </Route>

                                <Route path = "/profile">
                   <Profile user={user}/>
                </Route>

                        </>
                    )
                }

            </Switch>
        </Router>


        //v 6
