    return(
        <>

        <img src={user.profilePic} alt="" />
        <h1>
            Your name: {user.username}
        </h1>
        <h1>
            Your email: {user.email}
        </h1>
        {/* <h1>
            Your password: {user.user.password}
        </h1> */}

        {user.isAdmin && <h1> You are an admin</h1>}
        {!user.isAdmin && <h1> You are not an admin</h1>}

<Link to={`/otherusers/${user.user.username}`}>

        <h1>
            view other users
        </h1>
</Link>
        </>
    );
}

export default Profile;
