import "./listitem.scss"
import {
    PlayArrow,
    Add,
    ThumbUpAltOutlined,
    ThumbDownOutlined,
    ThumbDownAltOutlined,
  } from "@material-ui/icons";

import { useEffect, useState } from "react"
import React from 'react';


export default function Listitem({ index,item }) {

  const [isHovered, setIsHovered] = useState(false);
  const [movie, setMovie] = useState({});

  useEffect(() => {
    const getMovie = async () => {
      try {
        const res = await axios.get("/movies/find/" + item, {
          headers: {
            token:
            "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYxY2FhMzExNjRkODQ4M2MwMDNiODhhNCIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY0MTQyOTA4NiwiZXhwIjoxNjQxODYxMDg2fQ.TK8nzrJP0v0j9ajvvHYmsSmIdbwij_gi5F7mAzwluLU",
          },
        });
        setMovie(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    getMovie();
  }, [item]);

    return (
        <div className="listItem" 
        onMouseEnter = {() => setIsHovered(true)}
        onMouseLeave = {() => setIsHovered(false)}
        style={{left: isHovered && index * 225 +30}}

        >
             <img src= {item.img} alt=""/> 

{isHovered && (
        <>
          <video src={item.trailer}  autoPlay={true} muted loop  />

          <div className="itemInfo">
            <div className="icons">
              <PlayArrow className="icon" />
              <Add className="icon" />
              <ThumbUpAltOutlined className="icon" />
              <ThumbDownOutlined className="icon" />
            </div>
            <div className="itemInfoTop">
              <span>{item.duration}</span>
              <span className="limit">{item.limit}</span>
              <span>{item.year}</span>
            </div>
            <div className="desc">
              {item.desc}
            </div>
            <div className="genre">{item.genre}</div>
          </div>
        </>
      )}
        </div>
    )
}
