import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";


const Details=()=> {

    const [details,setDetails] = useState({});


    const url = "https://www.omdbapi.com/?apikey=5e1f4b00";
    const {imdbID} = useParams();

    const getDetails = async()=> {

        const response = await axios.get(`${url}&i=${imdbID}`)
        console.log(response.data)

        setDetails(response.data)
    }

    useEffect(()=>{
        getDetails()
    },[])

return(
<>
<div>
    <img src={details.Poster} alt="" />
    <h1>Movie Name: {details.Title}</h1>
    <h2>Movie Director: {details.Actors}</h2>
    <h2>Movie Cast: {details.Actors}</h2>
    <h2>Movie Genre: {details.Genre}</h2>

</div>
</>
);
}

export default Details;
