import './profile.css';
import { Link } from 'react-router-dom';

const Profile = (user) => {


    // console.log("user is : " + user);
    // console.log(user.user.username)

    const queryString = "otherusers"+user.user.username;

    return(
        <>

        <img src={user.user.profilePic} alt="" />
        <h1>
            Your name: {user.user.username}
        </h1>
        <h1>
            Your email: {user.user.email}
        </h1>
        {/* <h1>
            Your password: {user.user.password}
        </h1> */}

        {user.user.isAdmin && <h1> You are an admin</h1>}
        {!user.user.isAdmin && <h1> You are not an admin</h1>}

<Link to={`/otherusers/${user.user.username}`}>

        <h1>
            view other users
        </h1>
</Link>
        </>
    );
}

export default Profile;
